import sys,json
sightings = json.loads(sys.argv[1])
sightings_dict = {"sightings":[]}

# loop through all sighting of module
for sighting in sightings: 
  if "targets" in sighting.keys() and len(sighting["targets"]) > 0:
    if "sxo-sighting" in sighting["source"] or "securex-orchestration" in sighting["source"]:
      continue
    else: 
      # delete ID so sighting JSON can be used to create a non-ephemeral sighting
      del sighting["id"]
      sighting["source"] = sighting["source"] + " - sxo-sighting"
    # loop through targets' observables to look for best idenitifier 
    for target in sighting["targets"]:
        # walk through target types and assign priority to hostname or mac_address for incident title
        for observable in target["observables"]:
            if "hostname" in observable.values():
                temp_dict = {
                    "target_value": [observable["value"] for observable in target["observables"] if observable["type"] == "hostname"][0],
                    "target_type": "hostname",
                    "observed_time": target["observed_time"]["start_time"],
                    "sighting_json": sighting
                }
                sightings_dict["sightings"].append(temp_dict)
                break
            elif "mac_address" in target["observables"].values():
                temp_dict = {
                    "target_value": [observable["value"] for observable in target["observables"] if observable["type"] == "mac_address"][0],
                    "target_type": "mac_address",
                    "observed_time": target["observed_time"]["start_time"],
                    "sighting_json": sighting
                }
                sightings_dict["sightings"].append(temp_dict)
                break
            # if no hostname or mac, choose first target (make more advanced later)
            else:
                temp_dict = {
                    "target_value": observable[0]["value"],
                    "target_type": observable[0]["type"],
                    "observed_time": target["observed_time"]["start_time"],
                    "sighting_json": sighting
                }
                sightings_dict["sightings"].append(temp_dict)
                break
# dump all target sightings
sighting_json = json.dumps(sightings_dict)
